import React, { useState, useEffect } from "react";
import { Container, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Link } from "@mui/material";
import axios from "axios";

function PatientDashboard() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAppointments();
  }, []);

  // ✅ Fetch Patient's Appointments
  const fetchAppointments = () => {
    axios.get("http://localhost:5001/api/appointments/my-appointments", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    })
    .then((res) => {
      setAppointments(res.data);
      setLoading(false);
    })
    .catch((err) => {
      console.error("Error fetching appointments:", err);
      setLoading(false);
    });
  };

  const getFileUrl = (filePath) => {
    return `http://localhost:5001/${filePath}`;
  };

  return (
    <Container maxWidth="lg">
      <Paper style={{ marginTop: 20, padding: 20 }}>
        <Typography variant="h4" gutterBottom>
          My Appointments
        </Typography>
        {loading ? (
          <Typography>Loading...</Typography>
        ) : appointments.length > 0 ? (
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Doctor Name</TableCell>
                  <TableCell>Doctor Email</TableCell>
                  <TableCell>Date & Time</TableCell>
                  <TableCell>Medical Code</TableCell>
                  <TableCell>Description</TableCell>
                  <TableCell>File</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {appointments.map((appt) => (
                  <TableRow key={appt.id}>
                    <TableCell>{appt.doctor_name || "Not Available"}</TableCell>
                    <TableCell>{appt.doctor_email || "Not Available"}</TableCell>
                    <TableCell>{new Date(appt.date_time).toLocaleString()}</TableCell>
                    <TableCell>{appt.medical_code}</TableCell>
                    <TableCell>{appt.description}</TableCell>
                    <TableCell>
                      {appt.file_path ? (
                        <>
                          {/\.(jpg|jpeg|png|gif)$/i.test(appt.file_path) ? (
                            <a href={getFileUrl(appt.file_path)} target="_blank" rel="noopener noreferrer">
                              <img 
                                src={getFileUrl(appt.file_path)} 
                                alt="Uploaded file" 
                                style={{ maxWidth: "100px", maxHeight: "100px" }} 
                              />
                            </a>
                          ) : (
                            <a href={getFileUrl(appt.file_path)} target="_blank" rel="noopener noreferrer">
                              View File
                            </a>
                          )}
                        </>
                      ) : (
                        "No file"
                      )}
                    </TableCell>
                    <TableCell>{appt.status}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <Typography>No appointments found.</Typography>
        )}
      </Paper>
    </Container>
  );
}

export default PatientDashboard;
