import React, { useState, useEffect } from "react";
import { Container, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Link } from "@mui/material";
import axios from "axios";

function DoctorDashboard() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch full appointment details for the doctor
    axios.get("http://localhost:5001/api/appointments/my-appointments", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    })
    .then((res) => {
      setAppointments(res.data);
      setLoading(false);
    })
    .catch((err) => {
      console.error("Error fetching appointments:", err);
      setLoading(false);
    });
  }, []);

  // Helper function to build the URL to the file
  const getFileUrl = (filePath) => {
    return `http://localhost:5001/${filePath}`;
  };

  return (
    <Container maxWidth="lg">
      <Paper style={{ marginTop: 20, padding: 20 }}>
        <Typography variant="h4" gutterBottom>
          Doctor Dashboard
        </Typography>
        {loading ? (
          <Typography>Loading...</Typography>
        ) : appointments.length > 0 ? (
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Patient ID</TableCell>
                  <TableCell>Date & Time</TableCell>
                  <TableCell>Medical Code</TableCell>
                  <TableCell>Description</TableCell>
                  <TableCell>File</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {appointments.map((appt) => (
                  <TableRow key={appt.id}>
                    <TableCell>{appt.patient_id}</TableCell>
                    <TableCell>{new Date(appt.date_time).toLocaleString()}</TableCell>
                    <TableCell>{appt.medical_code}</TableCell>
                    <TableCell>{appt.description}</TableCell>
                    <TableCell>
                      {appt.file_path ? (
                        <>
                          {/* If the file is an image, show a preview */}
                          {/\.(jpg|jpeg|png|gif)$/i.test(appt.file_path) ? (
                            <Link href={getFileUrl(appt.file_path)} target="_blank" rel="noopener noreferrer">
                              <img 
                                src={getFileUrl(appt.file_path)} 
                                alt="Uploaded file" 
                                style={{ maxWidth: "100px", maxHeight: "100px" }} 
                              />
                            </Link>
                          ) : (
                            // Otherwise, provide a download/view link
                            <Link href={getFileUrl(appt.file_path)} target="_blank" rel="noopener noreferrer">
                              View File
                            </Link>
                          )}
                        </>
                      ) : (
                        "No file"
                      )}
                    </TableCell>
                    <TableCell>{appt.status}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <Typography>No appointments found.</Typography>
        )}
      </Paper>
    </Container>
  );
}

export default DoctorDashboard;
