import React, { useState, useEffect } from "react";
import { Container, Typography, Paper, FormControl, InputLabel, Select, MenuItem, TextField, Button } from "@mui/material";
import axios from "axios";

function AppointmentsPage() {
    const [doctorId, setDoctorId] = useState("");
    const [dateTime, setDateTime] = useState("");
    const [doctors, setDoctors] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5001/api/doctors", {
            headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } // remove headers if route is public
        })
        .then((res) => setDoctors(res.data))
        .catch((err) => console.error("Error fetching doctors:", err));
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:5001/api/appointments/book", 
                { doctor_id: doctorId, date_time: dateTime },
                { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
            );
            alert("Appointment booked successfully!");
        } catch (error) {
            console.error("Booking error:", error.response?.data || error.message);
            alert("Booking failed: " + (error.response?.data?.message || "Unknown error"));
        }
    };

    return (
        <Container maxWidth="sm">
            <Paper elevation={3} style={{ padding: "20px", marginTop: "50px" }}>
                <Typography variant="h4" align="center" gutterBottom>Book an Appointment</Typography>
                <form onSubmit={handleSubmit}>
                    <FormControl fullWidth margin="normal">
                        <InputLabel id="doctor-select-label">Select Doctor</InputLabel>
                        <Select
                            labelId="doctor-select-label"
                            value={doctorId}
                            label="Select Doctor"
                            onChange={(e) => setDoctorId(e.target.value)}
                            required
                        >
                            {doctors.map((doc) => (
                                <MenuItem key={doc.id} value={doc.id}>{doc.name}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <TextField 
                        label="Date & Time" 
                        type="datetime-local" 
                        fullWidth 
                        margin="normal" 
                        variant="outlined" 
                        onChange={(e) => setDateTime(e.target.value)} 
                        required 
                    />
                    <Button type="submit" variant="contained" color="primary" fullWidth style={{ marginTop: "20px" }}>
                        Book Appointment
                    </Button>
                </form>
            </Paper>
        </Container>
    );
}

export default AppointmentsPage;
